package com.imooc.controller;import com.imooc.pojo.Resource;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;@Controller@RequestMapping("ftl")public class FreemarkerController {    @Autowired    private Resource resource;    @RequestMapping("/index")    public String index(Model model){        model.addAttribute("resource",resource);        return "freemarker/index";    }}