package com.imooc.pojo;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.PropertySource;@Configuration@ConfigurationProperties(prefix="com.imooc.opensource")@PropertySource(value = "classpath:resource.properties")public class Resource {    private String name;    private String webSite;    private String language;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getWebSite() {        return webSite;    }    public void setWebSite(String webSite) {        this.webSite = webSite;    }    public String getLanguage() {        return language;    }    public void setLanguage(String language) {        this.language = language;    }    @Override    public String toString() {        return "Resource{" +                "name='" + name + '\'' +                ", webSite='" + webSite + '\'' +                ", language='" + language + '\'' +                '}';    }}