package com.imooc.pojo;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Date;@RestControllerpublic class UserController {    @RequestMapping("/getUser")    public User getUser() {        User user = new User();        user.setName("张三");        user.setPassword("111111");        user.setAge(23);        user.setBirthday(new Date());        return user;    }    @RequestMapping("/getUserJson")    public IMoocJSONResult getUserJson() {        User user = new User();        user.setName("imoo222c");        user.setPassword("111111");        user.setAge(23);        user.setBirthday(new Date());        return IMoocJSONResult.ok(user);    }}